$this->database()->query("CREATE TABLE IF NOT EXISTS `".Phpfox::getT('waytame_answer')."` ( `answer_id` int(10) NOT NULL AUTO_INCREMENT, `question_id` int(10) NOT NULL, `user_id` int(10) NOT NULL, `answer` mediumtext NOT NULL, `total_like` int(10) DEFAULT '0', `total_dislike` int(10) NOT NULL DEFAULT '0', `time_stamp` int(10) NOT NULL, PRIMARY KEY (`answer_id`) ) ENGINE=InnoDB DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;");
$this->database()->query("CREATE TABLE IF NOT EXISTS `".Phpfox::getT('waytame_notification')."` ( `notification_id` int(10) NOT NULL AUTO_INCREMENT, `user_id` int(10) NOT NULL, `message` varchar(250) NOT NULL, `day` varchar(50) DEFAULT NULL, `time_stamp` int(10) NOT NULL, PRIMARY KEY (`notification_id`) ) ENGINE=InnoDB DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;");
$this->database()->query("CREATE TABLE IF NOT EXISTS `".Phpfox::getT('waytame_question')."` ( `question_id` int(10) NOT NULL AUTO_INCREMENT, `user_id` int(10) NOT NULL, `question` mediumtext NOT NULL, `owner_answer` mediumtext NOT NULL, `is_expire` tinyint(1) NOT NULL DEFAULT '0', `privacy` tinyint(1) NOT NULL DEFAULT '0', `privacy_comment` tinyint(1) NOT NULL DEFAULT '0', `total_comment` int(10) NOT NULL DEFAULT '0', `total_view` int(10) NOT NULL DEFAULT '0', `total_answer` int(10) NOT NULL DEFAULT '0', `total_like` int(10) NOT NULL DEFAULT '0', `total_dislike` int(10) NOT NULL DEFAULT '0', `module_id` varchar(50) NOT NULL DEFAULT 'waytame', `time_stamp` int(10) NOT NULL, `expire_time` int(10) NOT NULL, PRIMARY KEY (`question_id`) ) ENGINE=InnoDB DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;");
